    private void btnCrearCuentaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCrearCuentaActionPerformed
        String usuario = txtRegistroUsuario.getText().strip();
        String contrasenna = txtRegistroContrasenna.getText().strip();
        //Validaciones:
        if(usuario.isEmpty() || contrasenna.isEmpty()){
            JOptionPane.showMessageDialog(null, "Debe rellenar todos los espacios.", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }
        
        Usuario objNuevoUsuario = new Usuario(usuario, contrasenna);
        
        if(objNuevoUsuario.getUsuarios().stream().anyMatch(u -> u.getNombreUsuario().equals(usuario))){
            JOptionPane.showMessageDialog(null, "El usuario ya existe. Intente con otro nombre.", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }
        else{
            try (BufferedWriter writer = new BufferedWriter(new FileWriter("Usuario.csv", true))){
                writer.write(usuario + "," + contrasenna + ",");
                writer.newLine();
                objNuevoUsuario.agregarUsuario(objNuevoUsuario);
                JOptionPane.showMessageDialog(null, "Listo! Su cuenta fue creada con exito. Vuelva a la pantalla de inicio.", "Exito", JOptionPane.INFORMATION_MESSAGE);
            }
            catch(IOException e){
                JOptionPane.showMessageDialog(null, "Algo salio mal al crear su cuenta, intentelo de nuevo.", "Error", JOptionPane.ERROR_MESSAGE);
            }
        }
        txtRegistroUsuario.setText("");
        txtRegistroContrasenna.setText("");
    }//GEN-LAST:event_btnCrearCuentaActionPerformed

